final List<dynamic> styles = ['.focus-trap {\n  display: none;\n  z-index: 2000;\n  position: fixed;\n  top: 0;\n  right: 0;\n  bottom: 0;\n  left: 0;\n}\n.focus-trap.visible {\n  display: block;\n}\n\n.menu {\n  z-index: 2001;\n  position: relative;\n  width: 100%;\n  opacity: 0;\n  height: 0;\n  background: transparent;\n  overflow-y: scroll;\n  -webkit-overflow-scrolling: touch;\n  transition: height 250ms ease, opacity 250ms ease;\n}\n.menu.opened {\n  opacity: 1;\n  height: 100%;\n}\n\n.item {\n  position: relative;\n  cursor: pointer;\n  padding: 16px;\n  background: transparent;\n  font-size: 16px;\n  color: rgba(255, 255, 255, 0.56);\n  user-select: none;\n}\n.item.selected {\n  background: rgba(0, 0, 0, 0.54);\n  color: #ffffff;\n}\n.item:hover {\n  background: rgba(0, 0, 0, 0.2);\n  color: rgba(255, 255, 255, 0.8);\n}\n'];