final List<dynamic> styles = ['.container {\n  position: relative;\n  width: 100%;\n  height: 225px;\n  background: linear-gradient(to top, #222222, #424242);\n}\n\n.placeholder {\n  position: absolute;\n  width: 100%;\n  height: 225px;\n  display: flex;\n  align-items: center;\n  justify-content: center;\n  z-index: 1;\n}\n.placeholder img {\n  width: 128px;\n  height: 128px;\n}\n\n.actual {\n  position: absolute;\n  object-fit: cover;\n  z-index: 2;\n  width: 100%;\n  height: 225px;\n  opacity: 0;\n  transition: opacity 750ms ease;\n}\n.actual.loaded {\n  opacity: 1;\n}\n\n@media only screen and (min-width: 1024px) {\n  .container, .placeholder, .actual {\n    height: 450px;\n  }\n}\n'];