final List<dynamic> styles = [':host {\n  display: flex;\n  color: rgba(255, 255, 255, 0.4);\n  justify-content: space-around;\n}\n\n.date-item {\n  flex: 1;\n  cursor: pointer;\n  height: 50px;\n  position: relative;\n  display: flex;\n  flex-direction: column;\n  justify-content: center;\n  background: #0F1734;\n  transition: background-color 0.2s ease;\n}\n.date-item p {\n  text-align: center;\n}\n.date-item .dayname {\n  color: #717DAD;\n  font-size: 12px;\n  font-weight: 500;\n  transition: color 0.2s ease;\n}\n.date-item .day {\n  color: #ffffff;\n  font-size: 20px;\n  font-weight: 300;\n  transition: color 0.2s ease, font-weight 0.2s ease;\n}\n\n.selected {\n  background: #FBBE35;\n}\n.selected .dayname, .selected .day {\n  color: #0d1732;\n}\n.selected .day {\n  font-weight: 500;\n}\n\n@media only screen and (min-width: 1024px) {\n  .date-item {\n    height: 60px;\n    margin-right: 3px;\n  }\n  .date-item:last-child {\n    margin-right: 0;\n  }\n  .date-item .day {\n    font-size: 25px;\n  }\n}\n'];